import { mapState, mapActions } from "vuex";
import { AudioPlayer } from "@liripeng/vue-audio-player";
import "@liripeng/vue-audio-player/lib/vue-audio-player.css";
import LyricScroll from "vue-lyric-scroll";

export default {
  name: "app",
  data() {
    return {
      audioSources: {},
      audioList: [],
      duration: {},
      timeupdate: {},
      currentTime: 0,
      isPlaying: false,
      xuzhuan: "Rotation",
      lyric: {},
      active:"lyric-active"

    };
  },
  components: {
    AudioPlayer,
    LyricScroll
  },
  watch: {
    isPlayinga(old, nw) {
      //监听正在播放的歌曲改变
      console.log(old);
    }
  },
  methods: {
    stopP() {
      this.isPlaying = false;
    },
    iPlaying() {
      this.isPlaying = true;
      let audio = this.$refs.audio;
    },
    onTimeUpdate(e) {
      this.currentTime = e.target.currentTime;
      console.log(this.currentTime);
    },
    getDuration() {
      let audio = this.$refs.audio;

      //此时可以获取到duration
      this.duration = audio.duration;
    },
        timeStrToNum(str) {
      const minute = Number(str.slice(0, 2))
      const second = Number(str.slice(3, 5))
      const minSec = Number(str.slice(6, 8))
      return minute * 60 + second + minSec / 100
    },
    // 将歌词字符串转换为对象，格式为{开始时间: 歌词, ...}
    lyricToObj(lyricStr) {


      const obj = {}
      let perLyric
      let time
      lyricStr.split('\n').forEach((item, idx) => {
        perLyric = item.slice(item.indexOf(']') + 1)
        if (perLyric) {
          time = this.timeStrToNum(item.slice(1, 9))
          obj[time] = perLyric
        }
      })
      return obj
    },
    updateTime(e) {
      this.currentTime = e.target.currentTime; //获取audio当前播放时间
    },

    onClickLeft() {
      this.$router.go(-1); //返回上一层
    },
    onBeforePlay(next) {

      next(); // 开始播放
      this.getDuration();
    },
    getlyric() {
      const self = this;
      console.log(this.$refs)
      this.$http
        .get("/api/lyric", { params: { id: this.$route.params.id } })
        .then(response => {
          this.lyric =self.lyricToObj(response.data.lrc.lyric) ;
        })
        .catch(error => {
          console.log("接口或处理逻辑出错");
        });
    },
    getMusicInfo() {
      const this_ = this;
      this.$http
        .get(this.host+"/song/url", { params: { id: this.$route.params.id } })
        .then(response => {
          for (const item of this.playList) {
            if (item.id == this.$route.params.id) {
              this.musicInfo = item;
            }
          }

          this.audioSources = {
            title: this.musicInfo.name,
            artist: this.musicInfo.ar[0].name,
            pic: this.musicInfo.al.picUrl + "?param=500y500",
            src: response.data.data[0].url,
            id: this.$route.params.id
          };
          this.audioList.push(response.data.data[0].url);
          this.$store.commit("addSongToHistory", this.audioSources);
        })
        .catch(error => {
          console.log("接口或处理逻辑出错");
        });
    }
  },
  mounted() {
    this.getMusicInfo();
    this.getlyric();
  },
  computed: {

    ...mapState(["playList", "playHistory"])
  }
};
