import List from "../components/List.vue";
import Tabb from "../components/Tabb.vue";
import Singer from "../components/Singer.vue";
import Vlist from "../components/Vlist.vue";
export default {
  name: "HelloWorld",
  data() {
    return {
      show: false,
      active: "home",
      picList: [],
      hotList: [],
      singerList: [],
      mvList: []
    };
  },
  components: {
    List,
    Tabb,
    Singer,
    Vlist
  },
  props: {
    msg: String
  },
  created() {
    this.init();
    this.getHotList();
    this.getSingerList();
    this.getVideoList();
  },
  methods: {
    async init() {
      const this_ = this;
      this.$http
        .get("/api/banner?type=2", { param: {} })
        .then(function(response) {
          this_.picList = response.data.banners.map(item => item.pic);
        })
        .catch(error => {
          console.log("接口或处理逻辑出错");
        });
    },
    async getHotList() {
      const this_ = this;
      this.$http
        .get("/api/personalized", { params: { limit: 10 } })
        .then(function(response) {
          this_.hotList = response.data.result;
        })
        .catch(error => {
          console.log("接口或处理逻辑出错");
        });
    },
    async getSingerList() {
      const this_ = this;
      this.$http
        .get("/api/artist/list", { params: { limit: 100, type: -1 } })
        .then(function(response) {
          this_.singerList = response.data.artists;
        })
        .catch(error => {
          console.log("接口或处理逻辑出错");
        });
    },
    async getVideoList() {
      const this_ = this;
      this.$http
        .get("/api/mv/first", { params: { limit: 100, type: -1 } })
        .then(function(response) {
          this_.mvList = response.data.data;
        })
        .catch(error => {
          console.log("接口或处理逻辑出错");
        });
    },
    showPopup() {
      this.show = true;
    }
  }
};
